import React from 'react'
import { Link } from 'react-router-dom'
import { CaretLeft } from 'react-bootstrap-icons'
import { PlusOutlined, LockOutlined, LoadingOutlined } from '@ant-design/icons'
import { Form, Upload, Input, message } from 'antd'

import { Button } from '../../components'
import './AddSneakers.scss'

const getBase64 = (img, callback) => {
    const reader = new FileReader();
    reader.addEventListener('load', () => callback(reader.result))
    reader.readAsDataURL(img)
};
const beforeUpload = (file) => {
    const isJpgOrPng = file.type === 'image/jpeg' || file.type === 'image/png'
    if (!isJpgOrPng) {
        message.error('You can only upload JPG/PNG file!')
    }
    const isLt2M = file.size / 1024 / 1024 < 2;
    if (!isLt2M) {
        message.error('Image must smaller than 2MB!')
    }
    return isJpgOrPng && isLt2M
};

const AddSneakers = () => {
    const [loading, setLoading] = React.useState(false)
    const [imageUrl, setImageUrl] = React.useState()

    const handleChange = (info) => {
        if (info.file.status === 'uploading') {
            setLoading(true)
            return;
        }
        if (info.file.status === 'done') {
            getBase64(info.file.originFileObj, (url) => {
                setLoading(false)
                setImageUrl(url)
            });
        }
    }

    const uploadButton = (
        <div style={{ width: 200, height: 200 }}>
            {loading ? <LoadingOutlined /> : <PlusOutlined />}
            <div
                style={{
                    marginTop: 8,
                }}
            >
                Добвить изображение
            </div>
        </div>
    )

    return (
        <div className='addsneakers'>
            <div className="addsneakers__header">
                <Link to='/' className='addsneakers__header--back'>
                    <CaretLeft />
                </Link>
                <h1>Добавить товар</h1>
            </div>
            <div className="addsneakers__form">
                <Form
                    name="addsneakers"
                    initialValues={{ remember: true }}
                >
                    <Upload
                        name="avatar"
                        withCredentials
                        listType="picture-card"
                        className="addsneakers__form-uploader"
                        showUploadList={false}
                        action="https://www.mocky.io/v2/5cc8019d300000980a055e76"
                        beforeUpload={beforeUpload}
                        onChange={handleChange}
                    >
                        {imageUrl ? (
                            <img
                                src={imageUrl}
                                alt="avatar"
                                style={{
                                    width: '100%',
                                }}
                            />
                        ) : (
                            uploadButton
                        )}
                    </Upload>
                    <Form.Item
                        name="Password"
                        rules={[{ required: true, message: 'Пожалуйста заполните это поле' }]}
                    >
                        <Input.Password
                            id='password'
                            prefix={<LockOutlined className="site-form-item-icon" />}
                            type="password"
                            placeholder="Пароль"
                        // value={formik.values.password}
                        // onChange={formik.handleChange}
                        // onBlur={formik.handleBlur}
                        />
                    </Form.Item>
                    <Form.Item>
                        <Button content='Войти' padding={10} borderRadius={10}></Button>
                    </Form.Item>
                </Form>
            </div>
        </div>
    )
}

export default AddSneakers
