import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'
import axios from '../../axios'

export const fetchAllCart = createAsyncThunk('card/fetchAllCart', async () => {
    try {
        const { data } = await axios.get('/user/allCard')
        return data
    } catch (error) {
        throw error.response.data
    }
})

const initialState = {
    cartItems: null,
    sum: 0,
    status: 'loading',
    error: null,
}

const cartSlice = createSlice({
    name: 'Cart',
    initialState,
    reducers: {
        setSum: (state) => {
            state.cartItems && (state.sum = state.cartItems.forEach(item => ++item.price))
        }
    },
    extraReducers: (bilding) => {
        bilding
            .addCase(fetchAllCart.fulfilled, (state, action) => {
                state.cartItems = action.payload
                state.status = 'loaded'
                state.error = null
                cartSlice.caseReducers.setSum()
            })
            .addCase(fetchAllCart.pending, (state) => {
                state.cartItems = null
                state.status = 'loading'
                state.error = null
                state.sum = 0
            })
            .addCase(fetchAllCart.rejected, (state, action) => {
                state.cartItems = null
                state.status = 'error'
                state.error = action.error
                state.sum = 0
            })
    }
})

export default cartSlice.reducer
export const { setSum } = cartSlice.actions
