import React from 'react'
import { Link } from 'react-router-dom'
import { LockOutlined, UserOutlined } from '@ant-design/icons'
import { Form, Input, message } from 'antd'
import { useDispatch } from 'react-redux'
import { useFormik } from 'formik'

import { fetchLogin } from '../../redux/slices/Auth'
import { Button } from '../../components'
import './Login.scss'

const Login = () => {
    const dispatch = useDispatch()

    const formik = useFormik({
        initialValues: {
            login: '',
            password: ''
        },
        onSubmit: async values => {
            await (dispatch(fetchLogin(values)).unwrap().then(data => {
                message.success(`Вы успешно авторизовались, ${data.name}!`, 1.3)
    
                if ('token' in data) {
                    localStorage.setItem('token', data.token)
                } else {
                    message.error('Не удалось авторизоваться', 1.3)
                }
            }).catch(error => {
                message.error(error.message, 1.3)
            }))
        }
    })

    const onSubmit = async (values) => {
        await (dispatch(fetchLogin(values)).unwrap().then(data => {
            message.success(`Вы успешно авторизовались, ${data.name}!`, 1.3)

            if ('token' in data) {
                localStorage.setItem('token', data.token)
            } else {
                message.error('Не удалось авторизоваться', 1.3)
            }
        }).catch(error => {
            message.error(error.message, 1.3)
        }))
    }

    return (
        <div className='login'>
            <h1>Авторизация</h1>
            <Form
                name="normal_login"
                className="login-form"
                initialValues={{ remember: true }}
                onFinish={onSubmit}
            >
                <Form.Item
                    name="login"
                    rules={[{ required: true, message: 'Пожалуйста заполните это поле' }]}
                >
                    <Input prefix={<UserOutlined className="site-form-item-icon" />} placeholder="Логин" />
                </Form.Item>
                <Form.Item
                    name="password"
                    rules={[{ required: true, message: 'Пожалуйста заполните это поле' }]}
                >
                    <Input.Password
                        prefix={<LockOutlined className="site-form-item-icon" />}
                        type="password"
                        placeholder="Пароль"
                    />
                </Form.Item>
                <Form.Item>
                    <Button content='Войти' padding={10} borderRadius={10}></Button>
                </Form.Item>
            </Form>
            <Link to='/auth/register'>Зарегестрироваться</Link>
        </div>
    )
}

export default Login
