import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'
import axios from '../../axios'

export const fetchAllCart = createAsyncThunk('card/fetchAllCart', async () => {
    try {
        const { data } = await axios.get('/user/allCard')
        return data
    } catch (error) {
        throw error.response.data
    }
})

const initialState = {
    cartItems: null,
    status: 'loading',
    error: null,
    totalPrice: 0
}

const calcTotalPrice = (cartItems) => {
    return cartItems ? cartItems.reduce((sum, item) => item.price + sum, 0) : 0
}

const cartSlice = createSlice({
    name: 'Cart',
    initialState,
    reducers: {
        calcTotalPrice: (state) => {
            state.totalPrice = state.cartItems ? state.cartItems.reduce((sum, item) => item.price + sum, 0) : 0
        },
        setAddCard: (state, action) => {
            state.cartItems ? state.cartItems.push({ ...action.payload }) : new Array(action.payload)
            state.totalPrice =  calcTotalPrice()
        },
        setRemoveCard: (state, actios) => {
            const newCartItems = state.cartItems.filter(item => item._id !== actios.payload)
            state.cartItems = newCartItems.length === 0 ? null : newCartItems
        }
    },
    extraReducers: (bilding) => {
        bilding
            .addCase(fetchAllCart.fulfilled, (state, action) => {
                state.cartItems = action.payload.length === 0 ? null : action.payload
                state.status = 'loaded'
                state.error = null
            })
            .addCase(fetchAllCart.pending, (state) => {
                state.cartItems = null
                state.status = 'loading'
                state.error = null
            })
            .addCase(fetchAllCart.rejected, (state, action) => {
                state.cartItems = null
                state.status = 'error'
                state.error = action.error
            })
    }
})

export const { setRemoveCard, setAddCard } = cartSlice.actions
export default cartSlice.reducer
