import React from 'react'
import { Heart, Plus, HeartFill, Check, X } from 'react-bootstrap-icons'
import { Link } from 'react-router-dom'
import classnames from 'classnames'
import PropTypes from 'prop-types'
import { message, Popconfirm } from 'antd'
import { useSelector, useDispatch } from 'react-redux'

import { setRemoveCard, setAddCard } from '../../redux/slices/Cart'
import axios from '../../axios'
import './Card.scss'

const Card = ({ _id, title, imgUrl, price, type, isLiked }) => {
    const dispatch = useDispatch()
    const { userData } = useSelector(state => state.auth)
    const [liked, setLiked] = React.useState(false)
    const [added, setAdded] = React.useState(false)
    const addedCard = userData && userData.cart.includes(_id)

    const onAdded = async card => {
        message.success(`${card.title} добавленны в корзину!`, 1.3)
        dispatch(setAddCard(card))
        setAdded(!added)
        await axios.post('/user/addCard', { cardId: card._id })
    }

    const onRemove = async id => {
        if (type === 'cart') {
            dispatch(setRemoveCard(id))
        }
        await axios.post('/user/removeCard', { cardId: id })
    }

    return (
        <div
            style={type === 'list' ? {
                width: 220,
                padding: 30,
                borderRadius: 40
            } : {
                width: '100%',
                padding: '10px 20px',
                borderRadius: 20,
                marginBottom: 20
            }}
            className={classnames('card', {
                'card-list': type === 'list',
                'card-cart': type === 'cart'
            })}
        >
            <div className="card__img">
                {type === 'list' &&
                    <div onClick={() => setLiked(!liked)} className={classnames('card__img--likeBtn', {
                        'card__img--likeBtn-liked': liked
                    })}>
                        {(liked || isLiked) ? <HeartFill color='#FF8585' /> : <Heart />}
                    </div>
                }
                <img src={'http://localhost:5555' + imgUrl} alt="Sneakers" />
            </div>
            <div className="card__title">
                <Link to={"/" + _id}>{title}</Link>
                {type === 'cart' && <b>{price} ₽</b>}
            </div>
            {type === 'list' ?
                <div className="card__bottom">
                    <div className="card__bottom--price">
                        <b>Цена:</b>
                        <p>{price} ₽</p>
                    </div>
                    <div onClick={() => onAdded({ _id, title, imgUrl, price })} className={classnames('card__bottom--addBtn', {
                        'card__bottom--addBtn-added': addedCard || added
                    })}>
                        {addedCard || added ? <Check color='#fff' /> : <Plus />}
                    </div>
                </div> :
                
                <div onClick={() => onRemove(_id)} className='card__bottom--remove'>
                    <X />
                </div>
            }
        </div>
    )
}

Card.propTypes = {
    title: PropTypes.string,
    imgUrl: PropTypes.string,
    price: PropTypes.number,
    type: PropTypes.string,
    isLiked: PropTypes.bool
}

export default Card
