import React from 'react'
import { Link } from 'react-router-dom'
import { LockOutlined, UserOutlined } from '@ant-design/icons'
import { Form, Input } from 'antd'
import { withFormik } from 'formik'
import { validateForm } from '../../utils'

import { Button } from '../../components'
import './Register.scss'

const Register = props => {
    const {
        values,
        touched,
        errors,
        handleChange,
        handleBlur,
        handleSubmit,
    } = props

    return (
        <div className='register'>
            <h1>Регистрация</h1>
            <Form
                name="normal_login"
                className="login-form"
                initialValues={{ remember: true }}
                onFinish={handleSubmit}
            >
                <Form name="horizontal_login" layout="inline" style={{ justifyContent: 'space-between', marginBottom: 20 }}>
                    <Form.Item
                        hasFeedback
                        style={{ width: '48%', margin: 0 }}
                        name="Firstname"
                        validateStatus={!touched.firstname ? '' : errors.firstname ? 'error' : 'success'}
                        rules={[{ required: true }]}
                    >
                        <Input
                            id='firstname'
                            prefix={<UserOutlined className="site-form-item-icon" />}
                            placeholder="Имя"
                            value={values.firstname}
                            onChange={handleChange}
                            onBlur={handleBlur}
                        />
                    </Form.Item>
                    <Form.Item
                        style={{ width: '48%', margin: 0 }}
                        name="Surname"
                        rules={[{ required: true, message: '' }]}
                    >
                        <Input prefix={<UserOutlined className="site-form-item-icon" />} placeholder="Фамилия" />
                    </Form.Item>
                </Form>
                <Form.Item
                    name="Login"
                    rules={[{ required: true, message: 'Пожалуйста заполните это поле!' }]}
                >
                    <Input prefix={<UserOutlined className="site-form-item-icon" />} placeholder="Придумайте логин" />
                </Form.Item>
                <Form.Item
                    name="Password"
                    rules={[{ required: true, message: 'Пожалуйста заполните это поле!' }]}
                >
                    <Input.Password
                        prefix={<LockOutlined className="site-form-item-icon" />}
                        type="password"
                        placeholder="Пароль"
                    />
                </Form.Item>
                <Form.Item
                    name="Confirmation"
                    rules={[{ required: true, message: 'Пожалуйста заполните это поле!' }]}
                >
                    <Input.Password
                        prefix={<LockOutlined className="site-form-item-icon" />}
                        type="password"
                        placeholder="Поддтвердите пароль"
                    />
                </Form.Item>
                <Form.Item>
                    <Button content='Зарегестрироваться' padding={10} borderRadius={10}></Button>
                </Form.Item>
            </Form>
            <Link to='/auth/login'>Авторизоваться</Link>
        </div>
    )
}

withFormik({
    mapPropsToValues: () => ({
        login: '',
        password: '',
        confirmation: '',
        firstname: '',
        surname: ''
    }),
    validate: values => {
        let errors = {}
        validateForm({ values, errors })

        return errors
    },
    handleSubmit: (values, { setSubmitting }) => {
        setTimeout(() => {
            alert(JSON.stringify(values, null, 2))
            setSubmitting(false)
        }, 1000)
    },
    displayName: "RegisterForm"
})(Register)

export default Register
