import { createSlice } from '@reduxjs/toolkit'

const initialState = {
    isVisibCart: false,
}

const visibSlice = createSlice({
    name: 'cart',
    initialState,
    reducers: {
        setIsVisibCard: (state, action) => { 
            state.isVisibCart = action.payload 
        },
        setIsVisibFullCard: (state, action) => { 
            state.isVisibFullCard = action.payload 
        }
    }
})

export default visibSlice.reducer
export const { setIsVisibCard, setIsVisibFullCard } = visibSlice.actions
